INSERT INTO `course_data` (`id`, `title`, `short_desc`, `long_desc`, `course_link`, `video_link`, `start_date`, `course_length`, `course_image`, `category`, `site`, `course_fee`, `language`, `certificate`, `university`, `time_scraped`) VALUES
(1, 'The Secret Power of Brands', 'Understand the role of brands and brand management in the modern world, and start developing your career in the sector.', 'Brands are the most potent commercial and cultural force on the planet. Think about McDonald’s, Apple, Manchester United, Tate, Google or even Harry Potter. This free online course will help you get under the skin of such brands, and ask how and why they become so powerful.', 'https://www.futurelearn.com/courses/the-secret-power-of-brands', 'http://view.vzaar.com/2958812.mp4', '2015-11-02', 6, 'https://ugc.futurelearn.com/uploads/images/ce/b8/regular_ceb87398-86f6-4633-b705-cd4ddff00223.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'UEA (University of East Anglia)', '2015-11-04 00:00:05'),
(2, 'The Mind is Flat: the Shocking Shallowness of Human Psychology', 'Make better personal and professional decisions, considering the psychological dimension to choices, with this free online course.', 'What are the forces shaping human behaviour? How do we think and decide? This course, from Professor Nick Chater and Warwick Business School, explores the origins of human rationality and irrationality.', 'https://www.futurelearn.com/courses/the-mind-is-flat', '', '2015-11-09', 6, 'https://ugc.futurelearn.com/uploads/images/12/3e/regular_123ecc58-cdac-4e5f-84b0-64a2964fcfdf.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'The University of Warwick', '2015-11-04 00:00:06'),
(3, 'Contract Management: Building Relationships in Business', 'Learn to build relationships and manage contracts successfully with this free online course backed by UK government and IACCM.', 'Contracts are fundamental to all business activities and relationships are fundamental to all contracts.', 'https://www.futurelearn.com/courses/contract-management', 'http://view.vzaar.com/2948275.mp4', '2015-11-09', 3, 'https://ugc.futurelearn.com/uploads/images/9c/20/regular_9c2020d8-f89d-4033-893a-6fbd365fac4b.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'University of Southampton', '2015-11-04 00:00:06'),
(4, 'Intercultural Communication', 'Learn to appreciate, adjust to, and work or study in different cultures, with this free online intercultural communication course.', 'Whether you’re engaging in business across borders or cultural regions, meeting other people through international travel, working in a diverse, global organisation or studying abroad, you can benefit from a better understanding of intercultural communication.', 'https://www.futurelearn.com/courses/intercultural-communication', 'http://view.vzaar.com/5346186.mp4', '2015-11-09', 5, 'https://ugc.futurelearn.com/uploads/images/73/7e/regular_737e6fa1-bed3-4711-b089-cc21677a10e8.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'Shanghai International Studies University (SISU)', '2015-11-04 00:00:07'),
(5, 'Developing Cultural Intelligence for Leadership', 'Find out what Cultural Intelligence (CQ) is, and why good leaders and managers need it, in this free online course.', 'Cultural Intelligence (CQ) is the natural evolution from the well-established notions of IQ (intelligence quotient) and EQ (emotional intelligence). Good leaders need all three to lead effectively.', 'https://www.futurelearn.com/courses/cultural-intelligence', 'http://view.vzaar.com/4735645.mp4', '2015-11-09', 4, 'https://ugc.futurelearn.com/uploads/images/db/f4/regular_dbf4d500-c8cd-4df9-b921-02d96132c009.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'Common Purpose', '2015-11-04 00:00:08'),
(6, 'How to Succeed at: Interviews', 'This free three week course provides the tools you need to succeed at interviews and land your dream job or course place.', 'This course will help you to succeed at interviews, whether you are applying for jobs or planning to study. Because being offered an interview can be quite daunting, we’ve put together a set of materials to help you prepare and be successful on the day.', 'https://www.futurelearn.com/courses/interviews', 'http://view.vzaar.com/3030237.mp4', '2015-11-16', 3, 'https://ugc.futurelearn.com/uploads/images/e1/6a/regular_e16aaa55-5f57-43f6-801f-cccc179f1e21.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'The University of Sheffield', '2015-11-04 00:00:09'),
(7, 'The Internet of Things', 'Learn how IoT works, and how to create a successful product or company using it, with this free online course.', 'Entrepreneurs love it, large corporations love it, universities and now even governments love it – welcome to the exciting and emerging world of the Internet of Things (IoT).', 'https://www.futurelearn.com/courses/internet-of-things', 'http://view.vzaar.com/4611259.mp4', '2015-11-23', 4, 'https://ugc.futurelearn.com/uploads/images/8e/44/regular_8e442a5c-4cee-4fd3-8f8f-ed04df2789cd.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'Kings College London', '2015-11-04 00:00:09'),
(8, 'Managing My Investments', 'Gain the skills to manage your investments: analyse portfolio risk and plan your pension, with this free online course.', 'Research shows that UK households are poor at investment management with a limited understanding of investment choices, risks and returns. The resultant ‘savings gap’ threatens a scenario where many cannot afford to retire.', 'https://www.futurelearn.com/courses/managing-my-investments', 'http://view.vzaar.com/2770520.mp4', '2016-01-04', 6, 'https://ugc.futurelearn.com/uploads/images/bc/02/regular_bc025666-072c-4378-9cf1-3bab52b6d7dc.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'The Open University', '2015-11-04 00:00:10'),
(9, 'Supply Chain Innovation: How Technology Can Create a Sustainable Future', 'Understand how new technologies can make supply chains more sustainable and learn how to deal with todays trends.', 'Every product that we use today is the result of a supply chain - a complex series of steps that turn raw materials into the final products we use and everything related with this. Managing these supply chains has become more and more a challenge, especially with all modern technologies and todays trends. The 24-hour economy, fast delivery of goods to your home, wanting to have customized products and of course the focus on sustainability are a few of these trends.', 'https://www.futurelearn.com/courses/supply-chain-innovation', 'http://view.vzaar.com/5282692.mp4', '2016-01-11', 6, 'https://ugc.futurelearn.com/uploads/images/c3/5f/regular_c35fa072-a305-4a64-8787-1529c1e13589.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'University of Twente', '2015-11-04 00:00:11'),
(10, 'Behind the Scenes at the 21st Century Museum', 'Get an introduction to museum studies with this free online course. Learn about the people and ideas that shape museums today', 'How can we understand museums today? Who are museums for and why are they working to engage new audiences? How do we respond emotionally to museum objects and spaces? And how can museums play a role in the pursuit of social justice, human rights, or health and wellbeing?', 'https://www.futurelearn.com/courses/museum', 'http://view.vzaar.com/2656218.mp4', '2016-01-18', 6, 'https://ugc.futurelearn.com/uploads/images/93/8b/regular_938bf370-e957-4471-b498-ea6fc66d3625.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'University of Leicester', '2015-11-04 00:00:12'),
(11, 'How to Build a Sustainable Fashion Business', 'Learn from leading entrepreneurs what it takes to create a successful fashion business, with a social or environmental ethos.', 'We’re the Ethical Fashion Forum, the global industry body for sustainable fashion. Since 2006, we’ve been helping fashion professionals achieve commercial success, while maximising benefits to communities and minimising negative environmental impacts.', 'https://www.futurelearn.com/courses/sustainable-fashion', '', '2016-01-18', 5, 'https://ugc.futurelearn.com/uploads/images/77/74/regular_7774142d-b953-47a4-b242-48414fcf9089.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'Creative Skillset', '2015-11-04 00:00:12'),
(12, 'Understanding Public Financial Management: How Is Your Money Spent?', 'How do governments acquire and spend public money? Take a fresh look at public financial management with this free online course.', 'What went wrong in Greece’s handling of its public debt? How does a city government like Detroit go from prosperity to bankruptcy? And how could the looting, theft and corruption in Malawi’s “cashgate” have been avoided?', 'https://www.futurelearn.com/courses/public-financial-management', 'http://view.vzaar.com/5441427.mp4', '2016-01-25', 4, 'https://ugc.futurelearn.com/uploads/images/ef/12/regular_ef1250d1-3017-48da-b50d-d032a15e5029.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'SOAS, University of London', '2015-11-04 00:00:14'),
(13, 'Digital Leadership: Creating Value Through Technology', 'Digital technologies are changing extraordinarily quickly. This four-week course will introduce you to the new digital landscape.', 'In this course you will learn how to:', 'https://www.futurelearn.com/courses/digital-leadership', '', '2016-02-08', 4, 'https://ugc.futurelearn.com/uploads/images/b8/88/regular_b888ab0c-c46e-4a35-afa8-9b946a74cfa0.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'University of Reading', '2015-11-04 00:00:14'),
(14, 'How to Succeed at: Writing Applications', 'This free three week course will help you produce a perfect CV, application and online profile when applying for a job or course.', 'This course will help you to write successful applications, whether you are applying for jobs or planning to study at university or college.', 'https://www.futurelearn.com/courses/writing-applications', 'http://view.vzaar.com/3030168.mp4', '2015-10-26', 3, 'https://ugc.futurelearn.com/uploads/images/fa/28/regular_fa284db2-d323-432d-8930-3ef8addefcb4.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'The University of Sheffield', '2015-11-04 00:00:15'),
(15, 'Digital Marketing: Challenges and Insights', 'Explore the latest trends in digital marketing. Get beneath the hype. What are the implications? How will you respond?', 'This short course introduces you to exciting new concepts and applications of digital marketing. It takes an informal “story telling” approach, encouraging you to share your own stories as consumers and/or marketers for the benefit of the learner group as a whole.', 'https://www.futurelearn.com/courses/digital-marketing', 'http://view.vzaar.com/1756128.mp4', '2015-10-19', 3, 'https://ugc.futurelearn.com/uploads/images/d2/75/regular_d27599ca-eb59-4fe1-a538-0b1ceffd3e10.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'University of Southampton', '2015-11-04 00:00:15'),
(16, 'Data to Insight: An Introduction to Data Analysis', 'This course is a hands-on introduction to statistical data analysis that emphasises fundamental concepts and practical skills.', 'Organisations around the world are collecting more data than ever. They are talking about big data, data science, business intelligence, analytics, data mining and data visualisation, and there’s an urgent need for people with the data skills to understand, interpret and communicate this information.', 'https://www.futurelearn.com/courses/data-to-insight', 'http://view.vzaar.com/1644667.mp4', '2015-10-19', 8, 'https://ugc.futurelearn.com/uploads/images/e5/09/regular_e50950a6-e653-4933-9443-6a25900440be.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'The University of Auckland', '2015-11-04 00:00:16'),
(17, 'Managing My Money', 'Gain the skills to manage your personal finances: managing budgets, debts, investments, property purchase, pensions and insurance.', 'With real incomes falling and personal debt at a record high, there has never been a better time to improve your personal financial skills – Managing My Money aims to do just that.', 'https://www.futurelearn.com/courses/managing-my-money', 'http://view.vzaar.com/2079404.mp4', '2015-10-12', 8, 'https://ugc.futurelearn.com/uploads/images/f6/33/regular_f633100f-bd6f-4c06-b320-14dc84fd0c5d.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'The Open University', '2015-11-04 00:00:17'),
(18, 'Managing People: Engaging Your Workforce', 'Learn how to understand and get the most out of people in the workplace, with a course thats ideal for those in management roles.', 'In this ten-week course you will learn how to get the most out of people in the workplace. Aimed at people who have, or are about to have, managerial responsibility in any type of organisation, this course will introduce you to reflective practice in people management.', 'https://www.futurelearn.com/courses/managing-people', 'http://view.vzaar.com/2165420.mp4', '2015-09-28', 10, 'https://ugc.futurelearn.com/uploads/images/9c/df/regular_9cdf858f-a6a0-4b45-8e81-6d5563f36f78.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'University of Reading', '2015-11-04 00:00:18'),
(19, 'Big Data: Measuring and Predicting Human Behaviour', 'Join us to explore how the vast amounts of data generated today can help us understand and even predict how humans behave.', 'We increasingly rely on networked computer systems and smart cards to support our everyday activities, and everything we do generates data – whether buying bread at the supermarket, taking a ride on public transport, or calling a friend for a chat.', 'https://www.futurelearn.com/courses/big-data', 'http://view.vzaar.com/2705064.mp4', '0000-00-00', 9, 'https://ugc.futurelearn.com/uploads/images/a7/57/regular_a7574c1e-db70-49e1-9060-9ba19d00f6f2.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'The University of Warwick', '2015-11-04 00:00:19'),
(20, 'Challenging Wealth and Income Inequality', 'Explore the concerns about rising generational and economic inequality in developed countries, with this free online course.', 'From religious leaders to heads of state, everyone is talking about economic inequality. What form can such inequality take in different countries? What impact does it have on society? And why should it matter to you?', 'https://www.futurelearn.com/courses/inequalities-in-personal-finance', 'http://view.vzaar.com/2763907.mp4', '0000-00-00', 4, 'https://ugc.futurelearn.com/uploads/images/c6/8f/regular_c68fc17f-f2b2-4b0d-b9d3-2ef55bb681d9.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'The Open University', '2015-11-04 00:00:19'),
(21, 'Decision Making in a Complex and Uncertain World', 'This course will teach you the first principles of complexity, uncertainty and how to make decisions in a complex world.', 'Leaders must be able to act in a complex world and under uncertainty. This course is a first step to develop yourself into one of the future’s key decision makers or to enhance your decision-making skills.', 'https://www.futurelearn.com/courses/complexity-and-uncertainty', 'http://view.vzaar.com/2182037.mp4', '0000-00-00', 6, 'https://ugc.futurelearn.com/uploads/images/f6/28/regular_f6280438-ac86-4f67-9ff2-6d5bdda3e9ea.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'University of Groningen', '2015-11-04 00:00:21'),
(22, 'Discovering Business in Society', 'Discover how businesses function and interact with the wider socio-economic environment. In association with ACCA.', 'This introductory course will guide your discovery of how businesses function and interact with the wider socio-economic environment.', 'https://www.futurelearn.com/courses/discovering-business-in-society', 'http://view.vzaar.com/1644198.mp4', '0000-00-00', 8, 'https://ugc.futurelearn.com/uploads/images/6d/d5/regular_6dd566bc-e4c4-4963-a1f4-5c75c7dd3789.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'University of Exeter', '2015-11-04 00:00:22'),
(23, 'How to Read Your Boss', 'How can we talk more effectively in the workplace? This course introduces linguistic techniques to enhance business communication.', 'Think about the conversations you have had in your workplace over the past few months. Do you come out of business meetings wishing you had said something differently, or felt misunderstood? Do you have difficulty talking to people more senior than you? What about when talking with other colleagues? ‘How to read your boss’ introduces you to the world of business communication through linguistics.', 'https://www.futurelearn.com/courses/how-to-read-your-boss', 'http://view.vzaar.com/1830475.mp4', '0000-00-00', 2, 'https://ugc.futurelearn.com/uploads/images/b9/8d/regular_b98d587b-67d0-4505-9d46-412697076740.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'The University of Nottingham', '2015-11-04 00:00:23'),
(24, 'Innovation and Enterprise', 'Managing the innovation process is neither a scientific process nor a black art. We will explore a model for innovation.', 'Introducing something new or innovating is easy in theory but hard in practice. New ideas can be plentiful, but selecting the best ideas and implementing them can be challenging. Managing the innovation process is neither a scientific process nor a black art. In addition to detailed research and planning, its success is influenced by human factors and, of course, luck!', 'https://www.futurelearn.com/courses/innovation-and-enterprise', 'http://view.vzaar.com/2559933.mp4', '0000-00-00', 6, 'https://ugc.futurelearn.com/uploads/images/a3/96/regular_a396712f-cca3-40a7-8c10-b3b828e69656.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'Loughborough University', '2015-11-04 00:00:24'),
(25, 'Innovation: the Key to Business Success', 'Understand commercial innovation, how ideas emerge and become reality, with this free online course developed with Marks & Spencer', 'This short free online course, which benefits from CPD accreditation, is designed for anyone interested in commercial innovation.', 'https://www.futurelearn.com/courses/innovation-the-key-to-business-success', 'http://view.vzaar.com/3398998.mp4', '0000-00-00', 3, 'https://ugc.futurelearn.com/uploads/images/74/39/regular_7439a4b9-39a0-40ab-afe9-bda32b604c9c.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'University of Leeds', '2015-11-04 00:00:24'),
(26, 'Make an Impact: Sustainability for Professionals', 'Find out how to integrate a sustainable development strategy into your company with this free online course', 'This free online course will cover the basic definitions and history of sustainable development and sustainability in business - from a niche interest to the present day.', 'https://www.futurelearn.com/courses/sustainability-for-professionals', 'http://view.vzaar.com/1413352.mp4', '0000-00-00', 6, 'https://ugc.futurelearn.com/uploads/images/31/d5/regular_31d5e65d-bb75-4eb9-978b-7f882a80e862.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'University of Bath', '2015-11-04 00:00:25'),
(27, 'The Enterprise Shed: Making Ideas Happen', 'Everyone is a thinker and a doer. Everyone is entrepreneurial. Turn your ideas into action with this free online course.', 'A park bench? A run? A workshop? A shed? We all have places where we do our best thinking and doing. This course aims to recreate that atmosphere, enabling you to unlock and explore your ideas, and make them happen.', 'https://www.futurelearn.com/courses/enterprise-shed', 'http://view.vzaar.com/2500131.mp4', '0000-00-00', 4, 'https://ugc.futurelearn.com/uploads/images/c5/06/regular_c506f3aa-4837-464b-8017-40e3f53c96ab.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'Newcastle University', '2015-11-04 00:00:26'),
(28, 'The Importance of Money in Business', 'Understand the role that money plays in business success and increase your career prospects with this free online course.', 'All organisations - whether multinationals, small and medium-sized enterprises, or not-for-profits - need money to be sustainable, and achieve their vision and objectives.', 'https://www.futurelearn.com/courses/money-in-business', 'http://view.vzaar.com/3990334.mp4', '0000-00-00', 4, 'https://ugc.futurelearn.com/uploads/images/89/06/regular_89068cb9-a682-4c6a-b72c-b3632d213c72.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'University of Leeds', '2015-11-04 00:00:27'),
(29, 'Understanding Modern Business and Organisations', 'Learn how knowledge, technology, ethics and globalisation affect businesses today, with this free online course for graduates.', 'Are you a graduate who’s new to the world of work? Would you like to understand some of the key trends affecting organisations today? Are you keen to think about how the workplace is likely to change in future?', 'https://www.futurelearn.com/courses/understanding-modern-business-and-organisations', 'http://view.vzaar.com/2321711.mp4', '0000-00-00', 6, 'https://ugc.futurelearn.com/uploads/images/7f/a3/regular_7fa33908-ff3d-43dc-8e6a-3fa05740c92a.jpg', 'Business & Management', 'FutureLearn.com', 0, 'english', 'yes', 'University of Strathclyde', '2015-11-04 00:00:28'),
(30, 'Improving Your Image: Dental Photography in Practice', 'Learn how to achieve consistent and excellent results in all aspects of dental photography with this free online course.', 'This free course will cover all aspects of the use of digital photography in dental practice: intra-oral, extra-oral, and portraits. Participants will be taken through photography, from the basics of choosing correct equipment and setting it up, to optimum settings, techniques for consistent imaging and the safe storage of images.', 'https://www.futurelearn.com/courses/dental-photography-in-practice', '', '2015-11-02', 4, 'https://ugc.futurelearn.com/uploads/images/ab/4f/regular_ab4fe1b5-2203-48de-85fa-ad0b5275d473.jpg', 'Media & Art', 'FutureLearn.com', 0, 'english', 'yes', 'University of Birmingham', '2015-11-04 00:00:29'),
(31, 'From Ink to Sound: Decoding Musical Manuscripts', 'Travel through the history of musical notation, learning how to decode medieval music manuscripts, with this free online course.', 'Nowadays music is all around us: we listen to it while we are on our way to work, when preparing lunch or even while showering. All this music is written down in its own language - the notation system. But did you ever wonder where this came from? Have you ever looked at a medieval music manuscript and wondered how to read it?', 'https://www.futurelearn.com/courses/from-ink-to-sound', 'http://view.vzaar.com/4770153.mp4', '2015-11-02', 7, 'https://ugc.futurelearn.com/uploads/images/f3/3b/regular_f33ba1e4-4371-4a57-a2b8-315ff6b9935b.jpg', 'Media & Art', 'FutureLearn.com', 0, 'english', 'yes', 'University of Basel', '2015-11-04 00:00:30'),
(32, 'Brand Storytelling: How to Use Narrative to Sell', 'Top creatives show you how to engage audiences through storytelling, from crafting a narrative to using digital.', 'We’re D&AD – a global organisation that inspires, celebrates and enables creative excellence worldwide. Discovering and nurturing talent is at the heart of what we do, supporting creatives throughout all stages of their careers.', 'https://www.futurelearn.com/courses/brand-storytelling', 'http://view.vzaar.com/5413793.mp4', '2015-11-16', 4, 'https://ugc.futurelearn.com/uploads/images/4f/4f/regular_4f4f1860-dd12-4458-9b81-2706e737d3b9.jpg', 'Media & Art', 'FutureLearn.com', 0, 'english', 'yes', 'D&AD', '2015-11-04 00:00:32'),
(33, 'Explore Animation', 'Learn animation techniques, including stop motion, 2D, CGI and pixilation, with award-winning animators, in this free NFTS course.', 'Whether you’re a budding animator or a fan of animation who wants to understand more about what happens behind-the-scenes, this free online course from the internationally acclaimed National Film and Television School (NFTS) will have something for you.', 'https://www.futurelearn.com/courses/explore-animation', 'http://view.vzaar.com/5558586.mp4', '2016-01-04', 5, 'https://ugc.futurelearn.com/uploads/images/c3/12/regular_c312b51c-993e-4a52-89af-6b75eaab67ae.jpg', 'Media & Art', 'FutureLearn.com', 0, 'english', 'yes', 'National Film and Television School', '2015-11-04 00:00:32'),
(34, 'Start Writing Fiction', 'This hands-on course helps you to get started with your own fiction writing, focusing on the central skill of creating characters.', 'Start Writing Fiction focuses on a skill which is central to the writing of all stories and novels – creating characters.', 'https://www.futurelearn.com/courses/start-writing-fiction', 'http://view.vzaar.com/2648205.mp4', '2016-01-18', 8, 'https://ugc.futurelearn.com/uploads/images/51/db/regular_51dbd3e2-84fa-4356-ad38-6b76738b6b03.jpg', 'Media & Art', 'FutureLearn.com', 0, 'english', 'yes', 'The Open University', '2015-11-04 00:00:34'),
(35, 'Behind the Scenes at the 21st Century Museum', 'Get an introduction to museum studies with this free online course. Learn about the people and ideas that shape museums today', 'How can we understand museums today? Who are museums for and why are they working to engage new audiences? How do we respond emotionally to museum objects and spaces? And how can museums play a role in the pursuit of social justice, human rights, or health and wellbeing?', 'https://www.futurelearn.com/courses/museum', 'http://view.vzaar.com/2656218.mp4', '2016-01-18', 6, 'https://ugc.futurelearn.com/uploads/images/93/8b/regular_938bf370-e957-4471-b498-ea6fc66d3625.jpg', 'Media & Art', 'FutureLearn.com', 0, 'english', 'yes', 'University of Leicester', '2015-11-04 00:00:36'),
(36, 'Transmedia Storytelling', 'Learn how to tell compelling stories across multiple platforms, like books, films, games and TV, with this free online course.', 'Stories are increasingly told across a variety of formats, such as books, comics, events, films, games and TV programmes - a trend known as transmedia storytelling or multiplatform storytelling.', 'https://www.futurelearn.com/courses/transmedia-storytelling', 'http://view.vzaar.com/5451882.mp4', '2016-01-18', 6, 'https://ugc.futurelearn.com/uploads/images/40/a7/regular_40a7200a-b643-4bea-b7e4-5e670de9533c.jpg', 'Media & Art', 'FutureLearn.com', 0, 'english', 'yes', 'Sungkyunkwan University (SKKU) ', '2015-11-04 00:00:37'),
(37, 'How to Build a Sustainable Fashion Business', 'Learn from leading entrepreneurs what it takes to create a successful fashion business, with a social or environmental ethos.', 'We’re the Ethical Fashion Forum, the global industry body for sustainable fashion. Since 2006, we’ve been helping fashion professionals achieve commercial success, while maximising benefits to communities and minimising negative environmental impacts.', 'https://www.futurelearn.com/courses/sustainable-fashion', '', '2016-01-18', 5, 'https://ugc.futurelearn.com/uploads/images/77/74/regular_7774142d-b953-47a4-b242-48414fcf9089.jpg', 'Media & Art', 'FutureLearn.com', 0, 'english', 'yes', 'Creative Skillset', '2015-11-04 00:00:37'),
(38, 'How to Write Your First Song', 'Get a practical introduction to the mechanics of songwriting and meet established songwriters with this free online course', 'Ever wanted to write a song, but not known how? This free online course will give you the practical and theoretical understanding you need to create a finished song of your own.', 'https://www.futurelearn.com/courses/songwriting', 'http://view.vzaar.com/5191801.mp4', '2016-01-25', 6, 'https://ugc.futurelearn.com/uploads/images/d8/67/regular_d8672b37-943a-46a4-89db-ef73ccad396f.jpg', 'Media & Art', 'FutureLearn.com', 0, 'english', 'yes', 'The University of Sheffield', '2015-11-04 00:00:38'),
(39, 'Teaching Literacy Through Film', 'Learn how to use film in the classroom, teach literacy and improve both reading and writing skills, with this free online course', 'Why use film to teach literacy? This free online course, led by film education experts from the British Film Institute (BFI) and film education charity Into Film, will examine the debate surrounding film as a vehicle for teaching literacy, alongside recent evidence demonstrating significant improvements in children’s reading and writing through use of film texts.', 'https://www.futurelearn.com/courses/teaching-literacy-through-film', 'http://view.vzaar.com/5531364.mp4', '2016-01-25', 4, 'https://ugc.futurelearn.com/uploads/images/24/cf/regular_24cf4d7a-2552-4a69-862c-32c3c941ad99.jpg', 'Media & Art', 'FutureLearn.com', 0, 'english', 'yes', 'The British Film Institute (BFI)', '2015-11-04 00:00:39'),
(40, 'Antiquities Trafficking and Art Crime', 'Delve into the seedy underbelly of the art world, looking at smuggling, theft, fakes, and fraud, with this free online course.', 'The devastation caused by the trafficking of illicit antiquities and the theft of art has gained widespread public attention in recent years.', 'https://www.futurelearn.com/courses/art-crime', 'http://view.vzaar.com/5348091.mp4', '2016-02-01', 3, 'https://ugc.futurelearn.com/uploads/images/a0/fd/regular_a0fd2545-9657-4fa4-9769-f4f910c9d9b7.jpg', 'Media & Art', 'FutureLearn.com', 0, 'english', 'yes', 'University of Glasgow', '2015-11-04 00:00:40'),
(41, 'Film Production: Behind the Scenes of Feature Filmmaking', 'Take a guided tour through the process of making a feature film and meet some of the people who make films every day.', 'Over three weeks, The Production Guild of Great Britain, guided by respected film and television professionals, will demystify the process of making a feature film. You will discover the key stages of how a film gets made and the different types of production.', 'https://www.futurelearn.com/courses/film-production', '', '2016-02-01', 3, 'https://ugc.futurelearn.com/uploads/images/25/bf/regular_25bfc285-4025-4cf9-ab8c-3255917e825a.jpg', 'Media & Art', 'FutureLearn.com', 0, 'english', 'yes', 'Creative Skillset', '2015-11-04 00:00:41'),
(42, 'Music Moves: Why Does Music Make You Move?', 'Learn about the psychology of music and movement, and how researchers study music-related movements, with this free online course.', 'Music is movement. A bold statement, but one that we will explore together in this free online course.', 'https://www.futurelearn.com/courses/music-moves', 'http://view.vzaar.com/5538653.mp4', '2016-02-01', 6, 'https://ugc.futurelearn.com/uploads/images/4e/8c/regular_4e8c81fa-95da-44c9-ab49-25619b0706ba.jpg', 'Media & Art', 'FutureLearn.com', 0, 'english', 'yes', 'University of Oslo', '2015-11-04 00:00:42'),
(43, 'An Introduction to Screenwriting', 'This free online course explores the key concepts and fundamental principles involved in the process of screenwriting.', 'Screenplays form the starting point for most dramatic films, the essential work from which all other filmmaking flows. All of the tender romance, terrifying action and memorable lines begin at the screenwriter’s desk. This free online course will introduce you to the basic elements and key concepts behind a professional screenplay.', 'https://www.futurelearn.com/courses/screenwriting', '', '2016-02-29', 2, 'https://ugc.futurelearn.com/uploads/images/d2/70/regular_d270e847-b243-4ba7-ade2-a3b4a551e420.jpg', 'Media & Art', 'FutureLearn.com', 0, 'english', 'yes', 'UEA (University of East Anglia)', '2015-11-04 00:00:43'),
(44, 'Critical Listening for Studio Production', 'This free online course is a technical ear training programme designed to improve critical listening in a music studio context.', 'There are many qualities required to be a skilled sound engineer: the theoretical knowledge of sound and audio, the technical mastery of studio hardware and software, and diplomatic people skills, for example. But perhaps the most obvious (and often overlooked) quality required of all sound engineers is the ability to listen.', 'https://www.futurelearn.com/courses/critical-listening-for-studio-production', '', '2015-10-26', 7, 'https://ugc.futurelearn.com/uploads/images/1e/8d/regular_1e8dd8dd-54e0-4c07-a4b1-530a752db8bd.jpg', 'Media & Art', 'FutureLearn.com', 0, 'english', 'yes', 'Queens University Belfast', '2015-11-04 00:00:44'),
(45, 'Explore Filmmaking: from Script to Screen', 'Learn from award-winning filmmakers about how films get from script to screen with this course from the NFTS and BFI Film Academy.', 'Whether you’re a budding filmmaker or a film fan who wants to understand more about what happens behind-the-scenes, this free online course from the internationally acclaimed National Film and Television School will have something for you.', 'https://www.futurelearn.com/courses/explore-filmmaking', 'http://view.vzaar.com/3489645.mp4', '2015-10-12', 6, 'https://ugc.futurelearn.com/uploads/images/41/34/regular_4134f444-1bba-424f-8a6f-808b533db39d.jpg', 'Media & Art', 'FutureLearn.com', 0, 'english', 'yes', 'National Film and Television School', '2015-11-04 00:00:45'),
(46, 'Start Writing Fiction', 'This hands-on course helps you to get started with your own fiction writing, focusing on the central skill of creating characters.', 'Start Writing Fiction focuses on a skill which is central to the writing of all stories and novels – creating characters.', 'https://www.futurelearn.com/courses/start-writing-fiction', 'http://view.vzaar.com/2648205.mp4', '2015-10-05', 8, 'https://ugc.futurelearn.com/uploads/images/2c/9b/regular_2c9b9500-3e8d-484c-9285-ada4803496de.jpg', 'Media & Art', 'FutureLearn.com', 0, 'english', 'yes', 'The Open University', '2015-11-04 00:00:45'),
